'use strict'

import React from 'react'
import {
  ApAccordion,
  ApAccordionHeader,
  ApAccordionBody,
  ApAccordionStyle
}from 'apeman-react-accordion'

const ExampleComponent = React.createClass({
  getInitialState () {
    return {
      open: false
    }
  },
  onToggle () {
    const s = this
    s.setState({
      open: !s.state.open
    })
  },
  render () {
    const s = this
    let {state} = s
    return (
      <div>
        <ApAccordionStyle/>
        <ApAccordion open={ state.open }>
          <ApAccordionHeader onToggle={ s.onToggle }>
            <a>This is Header text.</a>
          </ApAccordionHeader>
          <ApAccordionBody>
            <span>This is Body text</span>
          </ApAccordionBody>
        </ApAccordion>
      </div>
    )
  }
})
