/**
 * apeman react package for body component.
 * @class ApBody
 */

'use strict'

import React, {PropTypes as types} from 'react'
import classnames from 'classnames'

/** @lends ApBody */
const ApBody = React.createClass({
  // --------------------
  // Specs
  // --------------------

  propTypes: {},

  mixins: [],

  statics: {},

  getInitialState () {
    return {}
  },

  getDefaultProps () {
    return {}
  },

  render () {
    const s = this
    let { props } = s
    return (
      <body { ...props }
        className={ classnames('ap-body', props.className) }>{ props.children }</body>
    )
  }
})

export default ApBody
