/**
 * Button group component.
 * @class ApButtonGroup
 */

'use strict'

import React, {PropTypes as types} from 'react'
import classnames from 'classnames'

/** @lends ApButtonGroup */
const ApButtonGroup = React.createClass({

  // --------------------
  // Specs
  // --------------------

  propTypes: {
    /** Centered */
    centered: types.bool
  },

  mixins: [],

  getInitialState () {
    return {}
  },

  getDefaultProps () {
    return {}
  },

  render () {
    const s = this
    let { props } = s

    let className = classnames('ap-button-group', {
      'ap-button-group-centered': props.centered
    }, props.className)
    return (
      <div className={ className }
           style={ Object.assign({}, props.style) }
      >
        { props.children }
      </div>
    )
  }
})

export default ApButtonGroup
