/**
 * Row for Icon buttons.
 * @class ApIconButtonRow
 */

'use strict'

import React from 'react'
import classnames from 'classnames'

/** @lends ApIconButtonRow */
const ApIconButtonRow = React.createClass({

  // --------------------
  // Specs
  // --------------------

  propTypes: {},

  getInitialState () {
    return {}
  },

  getDefaultProps () {
    return {}
  },

  render () {
    const s = this
    let { props } = s
    return (
      <div className={ classnames('ap-icon-button-row', props.className) }>
        { props.children }
      </div>
    )
  }

})

export default ApIconButtonRow;


