/**
 * Next button component.
 * @class ApNextButton
 */

'use strict'

import React, {PropTypes as types} from 'react'
import classnames from 'classnames'
import ApButton from './ap_button'
import {ApIcon} from 'apeman-react-icon'
import {shallowEqual} from 'asobj'

/** @lends ApNextButton */
const ApNextButton = React.createClass({

  // --------------------
  // Specs
  // --------------------

  propTypes: {
    disabled: types.bool,
    onTap: types.func,
    text: types.string,
    size: types.number,
    icon: types.string
  },

  mixins: [
  ],

  getInitialState () {
    return {}
  },

  getDefaultProps () {
    return {
      disabled: false,
      onTap: null,
      text: null,
      icon: 'fa fa-caret-right'
    }
  },

  render () {
    const s = this
    let { props } = s
    return (
      <ApButton { ...props }
                className={ classnames('ap-next-button', props.className) }
                wide={ false }
                style={Object.assign({}, props.style)}
      >
                <span className='ap-next-button-text'>
                    { props.text }
                </span>
        { props.children }
        <ApIcon className={ classnames('ap-next-button-icon', props.icon) }/>
      </ApButton>
    )
  },

  // --------------------
  // Lifecycle
  // --------------------

  shouldComponentUpdate (nextProps, nextState) {
    const s = this
    let { props, state } = s
    return !shallowEqual(props, nextProps) || !shallowEqual(state, nextState)
  }

})

export default ApNextButton
