/**
 * Body for dialog.
 * @class ApDialogBody
 */

'use strict'

import React, {Component, PropTypes as types} from 'react'
import classnames from 'classnames'

/** @lends ApDialogBody */
class ApDialogBody extends Component {
  render () {
    const s = this,
      { props } = s
    return (
      <div className={ classnames('ap-dialog-body', props.className) }
           style={ Object.assign({}, props.style) }>
        { props.children }
      </div>
    )
  }
}

export default ApDialogBody;