/**
 * Header for dialog.
 * @class ApDialogHeader
 */

'use strict'

import React, {Component, PropTypes as types} from 'react'
import classnames from 'classnames'

/** @lends ApDialogHeader */
class ApDialogHeader extends Component {
  render () {
    const s = this
    let { props } = s
    return (
      <div className={ classnames('ap-dialog-header', props.className) }
           style={ Object.assign({}, props.style) }>
        { props.children }
      </div>
    )
  }
}

export default ApDialogHeader
