/**
 * Drop down item component
 * @class ApDropdownItem
 */

'use strict'

import React, {Component, PropTypes as types} from 'react'
import classnames from 'classnames'
import {withTouch} from 'apeman-react-touchable'

/** @lends ApDropdownItem */
class ApDropdownItem extends Component {
  render () {
    const s = this
    let { props } = s

    return (
      <a className={ classnames('ap-dropdown-item', props.className) }
         style={ Object.assign({}, props.style) }>
        { props.children }
      </a>
    )
  }
}

Object.assign(ApDropdownItem, {
  // --------------------
  // Specs
  // --------------------

  propTypes: {}
})

export default withTouch(ApDropdownItem)
