/**
 * Value of form field.
 * @class ApFieldValue
 */

'use strict'

import React, {Component, PropTypes as types} from 'react'
import classnames from 'classnames'

/** @lends ApFieldValue*/
class ApFieldValue extends Component {
  // --------------------
  // Specs
  // --------------------
  render () {
    const s = this
    let { props } = s

    return (
      <div className={ classnames('ap-field-value', props.className) }
      >
        { props.children }
      </div>
    )
  }
}

Object.assign(ApFieldValue, {
  // --------------------
  // Specs
  // --------------------
  propTypes: {},
  defaultProps: {}
})

export default ApFieldValue
