/**
 * Dropdown for header
 * @class ApHeaderDropdown
 */
'use strict'

import React, {PropTypes as types} from 'react'
import classnames from 'classnames'
import {ApDropdown} from 'apeman-react-dropdown'

/** @lends ApHeaderDropdown */
const ApHeaderDropdown = React.createClass({

  // --------------------
  // Specs
  // --------------------

  mixins: [],

  render () {
    const s = this
    let { props } = s
    return (
      <ApDropdown { ...props }
                  className={ classnames('ap-header-dropdown', props.className) }>
        { props.children }
      </ApDropdown>
    )
  }

})

export default ApHeaderDropdown
