/**
 * Tab for header.
 * @class ApHeaderTab
 */

'use strict'

import React, {PropTypes as types} from 'react'
import classnames from 'classnames'
import {ApTab} from 'apeman-react-tab'

/** @lends ApHeaderTab */
const ApHeaderTab = React.createClass({

  // --------------------
  // Specs
  // --------------------

  propTypes: {},

  mixins: [],

  statics: {},

  getInitialState () {
    return {}
  },

  getDefaultProps () {
    return {}
  },

  render () {
    const s = this
    let { props } = s
    return (
      <nav className='ap-header-tab-wrap'>
        <ApTab className={ classnames('ap-header-tab', props.className) }>
          { props.children }
        </ApTab>
      </nav>
    )
  }
})

export default ApHeaderTab
