/**
 * apeman react package for html component.
 * @class ApHtml
 */

'use strict'

import React from 'react'
import classnames from 'classnames'

/** @lends ApHtml */
const ApHtml = React.createClass({
  render () {
    const s = this
    let { props } = s
    return (
      <html { ...props }
        className={ classnames('ap-html', props.className) }>{ props.children }</html>
    )
  }
})

export default ApHtml
