/**
 * Style for ApHtml.
 * @class ApHtmlStyle
 */

'use strict'

import React, {PropTypes as types} from 'react'
import {ApStyle} from 'apeman-react-style'

/** @lends ApHtmlStyle */
const ApHtmlStyle = React.createClass({
  propTypes: {
    style: types.object
  },

  getDefaultProps () {
    return {}
  },
  statics: {
    styleData (config) {
      return {
        all: {
          '.ap-html': {
            padding: 0,
            margin: 0
          }
        }

      }
    }
  },
  render () {
    const s = this
    let { props } = s

    let { all, small, medium, large } = ApHtmlStyle.styleData(props)

    return (
      <ApStyle data={ Object.assign(all, props.style) }
               smallMediaData={ small }
               mediumMediaData={ medium }
               largeMediaData={ large }
      >{ props.children }</ApStyle>
    )
  }
})

export default ApHtmlStyle
