/**
 * apeman react package for main component.
 * @class ApMain
 */

'use strict'

import React, {PropTypes as types} from 'react'
import classnames from 'classnames'

/** @lends ApMain */
const ApMain = React.createClass({

  // --------------------
  // Specs
  // --------------------

  propTypes: {},

  mixins: [],

  statics: {},

  getInitialState () {
    return {}
  },

  getDefaultProps () {
    return {}
  },

  render () {
    const s = this
    let { props } = s
    let mainProps = Object.assign({}, props)
    return (
      <main { ... mainProps }
            className={ classnames('ap-main', props.className) }>
        { props.children }
      </main>
    )
  }

  // --------------------
  // Lifecycle
  // --------------------

  // ------------------
  // Helper
  // ------------------

  // ------------------
  // Private
  // ------------------
})

export default ApMain
