#!/usr/bin/env node

/**
 * Compile files.
 */

'use strict'

process.chdir(`${__dirname}/..`)

const { runTasks } = require('ape-tasking')
const { compileReactJsx } = require('ape-compiling')

runTasks('compile', [
  () => {
    let libDir = `${__dirname}/../lib`
    return compileReactJsx('*.jsx', {
      cwd: libDir,
      out: libDir,
      map: 'inline'
    })
  }
], true)
