/**
 * Container element for page.
 * @class ApPageContainer
 */

'use strict'

import React, {PropTypes as types} from 'react'
import classnames from 'classnames'

/** @lends ApPageContainer */
const ApPageContainer = React.createClass({

  // --------------------
  // Specs
  // --------------------

  propTypes: {
    id: types.string.isRequired
  },

  mixins: [],

  statics: {},

  getInitialState () {
    return {}
  },

  getDefaultProps () {
    return {}
  },

  render () {
    const s = this
    let { state, props } = s

    return (
      <div className={ classnames('ap-page-container', props.className)}
           id={props.id}
           style={Object.assign({}, props.style) }>
        { props.children }
      </div>
    )
  },

  // --------------------
  // Lifecycle
  // --------------------

  // ------------------
  // Helper
  // ------------------

  // ------------------
  // Private
  // ------------------
})

export default ApPageContainer
