'use strict'

import React from 'react'
import {ApPager, ApPagerStyle} from 'apeman-react-pager'

const ExampleComponent = React.createClass({
  getInitialState () {
    return {
      page: 2
    }
  },
  render () {
    const s = this
    let { state } = s
    return (
      <div>
        <ApPagerStyle highlightColor="#38E"/>
        <ApPager total={ 8 }
                 page={ state.page }
                 size={ 3 }
                 onChange={ (e) => s.setState({ page:e.page }) }
        />
      </div>
    )
  }
})
