/**
 * Body for a section.
 * @class ApSectionBody
 */

'use strict'

import React, {PropTypes as types} from 'react'
import classnames from 'classnames'

/** @lends ApSectionBody */
const ApSectionBody = React.createClass({

  // --------------------
  // Specs
  // --------------------

  propTypes: {},

  mixins: [],

  statics: {},

  getInitialState () {
    return {}
  },

  getDefaultProps () {
    return {}
  },

  render () {
    const s = this
    let { props } = s

    return (
      <div className={ classnames('ap-section-body', props.className) }
           style={ Object.assign({}, props.style) }>
        { props.children }
      </div>
    )
  }
})

export default ApSectionBody
