'use strict'

import React from 'react'
import {ApSelect, ApSelectStyle} from 'apeman-react-select'

const ExampleComponent = React.createClass({

  getInitialState () {
    const s = this
    return {
      value: 'banana'
    }
  },

  render () {
    const s = this
    let { state } = s
    return (
      <div>
        <ApSelect options={ {
                    banana: 'Yes, banana!',
                    orange: 'Of course orange!',
                    apple: 'Nothing but apple!'
                } }
                  value={ state.value }
                  onChange={ s._handleChange }
        />
      </div>
    )
  },

  _handleChange (e) {
    const s = this
    let { value } = e.target
    s.setState({
      value: value
    })
  }
})
