/**
 * Slider label component.
 * @class ApSliderLabel
 */

'use strict'

import React, {PropTypes as types} from 'react'
import classnames from 'classnames'

/** @lends ApSliderLabel */
const ApSliderLabel = React.createClass({
  // --------------------
  // Specs
  // --------------------
  propTypes: {
    value: types.number
  },

  getDefaultProps () {
    return {
      value: null
    }
  },

  render () {
    const s = this
    let { props } = s

    return (
      <label className={ classnames('ap-slider-label', props.className) }>
        <span>{ props.value }</span>
      </label>
    )
  }
})

export default ApSliderLabel
