'use strict'

import React from 'react'
import {ApSwitch, ApSwitchStyle} from 'apeman-react-switch'

const ExampleComponent = React.createClass({
  render () {
    const s = this
    let { state } = s
    let { on } = state
    return (
      <div>
        <ApSwitchStyle />
        <ApSwitchStyle highlightColor="#b35600"/>
        <ApSwitch on={ on }
                  onTap={ s._toggleSwitch }
        />
      </div>
    )
  },

  _toggleSwitch () {
    const s = this
    let { state } = s
    let { on } = state
    s.setState({ on: !on })
  }
})

