/**
 * Tab component.
 * @class ApTab
 */

'use strict'

import React, {PropTypes as types} from 'react'
import classnames from 'classnames'

/** @lends ApTab */
const ApTab = React.createClass({

  // --------------------
  // Specs
  // --------------------

  propTypes: {},

  mixins: [],

  statics: {},

  getInitialState () {
    return {}
  },

  getDefaultProps () {
    return {}
  },

  render () {
    const s = this
    let { props } = s
    return (
      <div className={ classnames('ap-tab', props.className) }>
        { props.children }
      </div>
    )
  }

})

export default ApTab;