/**
 * Content of tab
 * @class ApTabContent
 */
'use strict'

import React, {PropTypes as types} from 'react'
import classnames from 'classnames'

/** @lends ApTabContent */
const ApTabContent = React.createClass({

  // --------------------
  // Specs
  // --------------------

  propTypes: {
    /** The tab is selected or not */
    selected: types.bool
  },

  statics: {},

  getInitialState () {
    return {}
  },

  render () {
    const s = this
    let { props } = s
    let className = classnames('ap-tab-content', props.className, {
      'ap-tab-content-selected': !!props.selected
    })
    let style = Object.assign({}, props.style)
    return (
      <div className={ className }
           style={ style }
      >
        { props.children }
      </div>
    )
  }

})

module.exports = ApTabContent
