/**
 * Group of tab
 * @class ApTabGroup
 */
'use strict'

import React, {PropTypes as types} from 'react'
import classnames from 'classnames'

/** @lends ApTabGroup */
const ApTabGroup = React.createClass({

  // --------------------
  // Specs
  // --------------------

  propTypes: {},

  statics: {},

  getInitialState () {
    return {}
  },

  render () {
    const s = this
    let { props } = s
    let className = classnames('ap-tab-group', props.className)
    let style = Object.assign({}, props.style)
    return (
      <div className={ className }
           style={ style }
      >
        { props.children }
      </div>
    )
  }

})

module.exports = ApTabGroup
