/**
 * Tag components.
 * @class ApTag
 */

'use strict'

import React, { PropTypes as types } from 'react'
import classnames from 'classnames'

/** @lends ApTag */
const ApTag = React.createClass({

  // --------------------
  // Specs
  // --------------------

  propTypes: {},

  render () {
    const s = this
    let { props } = s

    return (
      <span className={ classnames('ap-tag', props.className)}
            style={Object.assign({}, props.style) }>
                <span className="ap-tag-corner"/>
                <span className="ap-tag-dot"/>
                <span className="ap-tag-text"/>
        { props.children }
            </span>
    )
  }
})

export default ApTag;