'use strict'

import React from 'react'
import {ApToggle, ApToggleStyle} from 'apeman-react-toggle'

const ExampleComponent = React.createClass({
  getInitialState () {
    return {
      value: 'hoge'
    }
  },
  render () {
    const s = this
    return (
      <div>
        <ApToggleStyle/>
        <ApToggle value={ s.state.value }
                  options={ {
                            'hoge': 'This is hoge label',
                            'fuge': <span>This is <b>Foo</b></span>
                          } }
        />
      </div>
    )
  }
})

