/**
 * apeman react package for toggle components.
 * @class ApToggleItem
 */

'use strict'

import React, {Component, PropTypes as types} from 'react'
import classnames from 'classnames'
import {clone} from 'asobj'
import {ApButton} from 'apeman-react-button'

/** @lends ApToggleItem */
class ApToggleItem extends Component {
  render () {
    const s = this
    let { props } = s
    let className = classnames('ap-toggle-item', {
      'ap-toggle-item-active': props.active
    }, props.className)
    let buttonProps = clone(props, {
      without: 'active'
    })
    return (
      <ApButton { ...buttonProps }
                className={ className }
                style={ Object.assign({}, props.style) }
      >
        { props.children }
      </ApButton>
    )
  }
}

Object.assign(ApToggleItem, {
  // --------------------
  // Specs
  // --------------------

  propTypes: {
    active: types.bool
  },
  defaultProps: {}

})

export default ApToggleItem
