const React = require('react');
const ReactDOM = require('react-dom');
const FlashcardApp = require('react-flashcard-app');

const data =
  {
    id: 1,
    name: 'Example Deck',
    cards: [
      'Lorem', 'dolor',
      'sit', 'amet',
      'consetetur', 'sadipscing',
      'sed', 'diam',
    ],
  };
// options control which buttons should be included at which position
// in the toolbars. Also the button texts can be set via options
const options =
  {
    topControlBar: [
      'decrement', 'remove', 'undoRemove', 'increment',
    ],
    bottomControlBar: [
      'flip', 'shuffle', 'reset', 'revert',
    ],
    buttonTexts: {
      undoRemove: 'undo remove',
    },
  };

const App = () =>
  <div>
    <FlashcardApp data={data} options={options} />
  </div>;

ReactDOM.render(<App />, document.getElementById('app'));
