import React from 'react';
import PropTypes from 'prop-types';
import { Provider } from 'react-redux';
import App from './containers/app';
import configureStore from './configureStore';

const store = configureStore();

const FlashcardApp = props =>
  <Provider store={store}><App data={props.data} options={props.options} /></Provider>;

FlashcardApp.propTypes = {
  data: PropTypes.object.isRequired,
  options: PropTypes.object,
};

FlashcardApp.defaultProps = {
  options: {
    topControlBar: [
      'decrement', 'undoRemove', 'remove', 'increment',
    ],
    bottomControlBar: [
      'flip', 'reset', 'shuffle', 'revert',
    ],
    buttonTexts: {},
  },
};

export default FlashcardApp;
