export const initApp = data => (
  {
    type: 'INIT_APP',
    data,
  }
);

export const increment = () => (
  {
    type: 'INCREMENT',
  }
);

export const decrement = () => (
  {
    type: 'DECREMENT',
  }
);

export const flip = () => (
  {
    type: 'FLIP',
  }
);

export const revert = () => (
  {
    type: 'REVERT',
  }
);

export const remove = () => (
  {
    type: 'REMOVE_CARD',
  }
);

export const undoRemove = () => (
  {
    type: 'UNDO_REMOVE',
  }
);

export const reset = () => (
  {
    type: 'RESET',
  }
);

export const shuffle = () => (
  {
    type: 'SHUFFLE',
  }
);
