import React from 'react';
import DecrementButton from '../containers/control_buttons/decrement_button';
import IncrementButton from '../containers/control_buttons/increment_button';
import UndoRemoveButton from '../containers/control_buttons/undo_remove_button';
import RemoveButton from '../containers/control_buttons/remove_button';
import FlipButton from '../containers/control_buttons/flip_button';
import ResetButton from '../containers/control_buttons/reset_button';
import ShuffleButton from '../containers/control_buttons/shuffle_button';
import RevertButton from '../containers/control_buttons/revert_button';

const buttons = (button, text, index) => {
  switch (button) {
    case 'flip':
      return <FlipButton key={index} text={text} />;
    case 'reset':
      return <ResetButton key={index} text={text} />;
    case 'shuffle':
      return <ShuffleButton key={index} text={text} />;
    case 'revert':
      return <RevertButton key={index} text={text} />;
    case 'remove':
      return <RemoveButton key={index} text={text} />;
    case 'undoRemove':
      return <UndoRemoveButton key={index} text={text} />;
    case 'decrement':
      return <DecrementButton key={index} text={text} />;
    case 'increment':
      return <IncrementButton key={index} text={text} />;
    default:
      return null;
  }
};

export default buttons;
