import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import { decrement } from '../../actions';
import ActionButton from '../../components/action_button';

const DecrementButton = props =>
  <ActionButton
    disabled={props.total < 2}
    onSubmit={props.decrement}
    className='decrement'
    text={props.text}
  />;

DecrementButton.propTypes = {
  text: PropTypes.string,
  total: PropTypes.number.isRequired,
  decrement: PropTypes.func.isRequired,
};

DecrementButton.defaultProps = {
  text: '<<',
};

const mapStateToProps = state => ({
  removed: state.app.removed,
  total: state.app.deck.length - state.app.removed.length,
});

const mapDispatchToProps = dispatch => (
  bindActionCreators({ decrement }, dispatch)
);

export default connect(mapStateToProps, mapDispatchToProps)(DecrementButton);
