import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import { flip } from '../../actions';
import ActionButton from '../../components/action_button';

const FlipButton = props =>
  <ActionButton
    disabled={props.total < 2}
    onSubmit={props.flip}
    className='flip'
    text={props.text}
  />;

FlipButton.propTypes = {
  text: PropTypes.string,
  total: PropTypes.number.isRequired,
  flip: PropTypes.func.isRequired,
};

FlipButton.defaultProps = {
  text: 'flip',
};

const mapStateToProps = state => ({
  removed: state.app.removed,
  total: state.app.deck.length - state.app.removed.length,
});

const mapDispatchToProps = dispatch => (
  bindActionCreators({ flip }, dispatch)
);

export default connect(mapStateToProps, mapDispatchToProps)(FlipButton);
