import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import { increment } from '../../actions';
import ActionButton from '../../components/action_button';

const IncrementButton = props =>
  <ActionButton
    disabled={props.total < 2}
    onSubmit={props.increment}
    className='increment'
    text={props.text}
  />;

IncrementButton.propTypes = {
  text: PropTypes.string,
  total: PropTypes.number.isRequired,
  increment: PropTypes.func.isRequired,
};

IncrementButton.defaultProps = {
  text: '>>',
};

const mapStateToProps = state => ({
  removed: state.app.removed,
  total: state.app.deck.length - state.app.removed.length,
});

const mapDispatchToProps = dispatch => (
  bindActionCreators({ increment }, dispatch)
);

export default connect(mapStateToProps, mapDispatchToProps)(IncrementButton);
