import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import { remove } from '../../actions';
import ActionButton from '../../components/action_button';

const RemoveButton = props =>
  <ActionButton
    disabled={props.total < 1}
    onSubmit={props.remove}
    className='remove'
    text={props.text}
  />;

RemoveButton.propTypes = {
  text: PropTypes.string,
  total: PropTypes.number.isRequired,
  remove: PropTypes.func.isRequired,
};

RemoveButton.defaultProps = {
  text: 'remove',
};

const mapStateToProps = state => ({
  removed: state.app.removed,
  total: state.app.deck.length - state.app.removed.length,
});

const mapDispatchToProps = dispatch => (
  bindActionCreators({ remove }, dispatch)
);

export default connect(mapStateToProps, mapDispatchToProps)(RemoveButton);
