import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import { reset } from '../../actions';
import ActionButton from '../../components/action_button';

const ResetButton = props =>
  <ActionButton
    disabled={props.removed.length < 1}
    onSubmit={props.reset}
    className='reset'
    text={props.text}
  />;

ResetButton.propTypes = {
  text: PropTypes.string,
  removed: PropTypes.arrayOf(PropTypes.number).isRequired,
  reset: PropTypes.func.isRequired,
};

ResetButton.defaultProps = {
  text: 'reset',
};

const mapStateToProps = state => ({
  removed: state.app.removed,
  total: state.app.deck.length - state.app.removed.length,
});

const mapDispatchToProps = dispatch => (
  bindActionCreators({ reset }, dispatch)
);

export default connect(mapStateToProps, mapDispatchToProps)(ResetButton);
