import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import { revert } from '../../actions';
import ActionButton from '../../components/action_button';

const RevertButton = props =>
  <ActionButton
    disabled={props.total < 2}
    onSubmit={props.revert}
    className='revert'
    text={props.text}
  />;

RevertButton.propTypes = {
  text: PropTypes.string,
  total: PropTypes.number.isRequired,
  revert: PropTypes.func.isRequired,
};

RevertButton.defaultProps = {
  text: 'revert',
};

const mapStateToProps = state => ({
  removed: state.app.removed,
  total: state.app.deck.length - state.app.removed.length,
});

const mapDispatchToProps = dispatch => (
  bindActionCreators({ revert }, dispatch)
);

export default connect(mapStateToProps, mapDispatchToProps)(RevertButton);
