import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import { shuffle } from '../../actions';
import ActionButton from '../../components/action_button';

const ShuffleButton = props =>
  <ActionButton
    disabled={props.total < 2}
    onSubmit={props.shuffle}
    className='shuffle'
    text={props.text}
  />;

ShuffleButton.propTypes = {
  text: PropTypes.string,
  total: PropTypes.number.isRequired,
  shuffle: PropTypes.func.isRequired,
};

ShuffleButton.defaultProps = {
  text: 'shuffle',
};

const mapStateToProps = state => ({
  removed: state.app.removed,
  total: state.app.deck.length - state.app.removed.length,
});

const mapDispatchToProps = dispatch => (
  bindActionCreators({ shuffle }, dispatch)
);

export default connect(mapStateToProps, mapDispatchToProps)(ShuffleButton);
