import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import { undoRemove } from '../../actions';
import ActionButton from '../../components/action_button';

const UndoRemoveButton = props =>
  <ActionButton
    disabled={props.removed.length < 1}
    onSubmit={props.undoRemove}
    className='undoRemove'
    text={props.text}
  />;

UndoRemoveButton.propTypes = {
  text: PropTypes.string,
  undoRemove: PropTypes.func.isRequired,
  removed: PropTypes.arrayOf(PropTypes.number).isRequired,
};

UndoRemoveButton.defaultProps = {
  text: 'undo',
};

const mapStateToProps = state => ({
  removed: state.app.removed,
});

const mapDispatchToProps = dispatch => (
  bindActionCreators({ undoRemove }, dispatch)
);

export default connect(mapStateToProps, mapDispatchToProps)(UndoRemoveButton);
