const card = (state = {}, action) => {
  switch (action.type) {
    case 'SELECT_DECK':
    case 'REMOVE_CARD':
    case 'UNDO_REMOVE':
    case 'INCREMENT':
    case 'DECREMENT':
    case 'SHUFFLE':
      return { ...state, isFlipped: false };
    case 'RESET':
      return { ...state, isReverted: false, isFlipped: false };
    case 'REVERT':
      return { ...state, isReverted: !state.isReverted, isFlipped: false };
    case 'FLIP':
      return { ...state, isFlipped: !state.isFlipped };
    default:
      return state;
  }
};

export default card;
