export const getPairs = data => (
  data.cards.map((s, i) => (
    {
      id: parseInt(i / 2, 10),
      values: [s, data.cards[i + 1]],
    }
  )).filter((s, i) => i % 2 === 0)
);

export const initialState = data => (
  {
    removed: [],
    cardIndex: 0,
    isFlipped: false,
    isReverted: false,
    data,
  }
);

