import { expect } from 'chai';
import { describe, it } from 'mocha';
import reducer from '../src/reducers/card';

describe('app reducer', () => {
  it('returns the initial state', () => {
    expect(
      reducer(undefined, {}),
    ).eql({});
  });

  it('handles SHUFFLE', () => {
    expect(
      reducer({ isFlipped: true }, { type: 'SHUFFLE' }),
    ).eql({ isFlipped: false });
  });

  it('handles RESET', () => {
    expect(
      reducer({ isFlipped: true }, { type: 'RESET' }),
    ).eql({ isFlipped: false, isReverted: false });
  });

  it('handles REVERT', () => {
    expect(
      reducer({ isFlipped: true, isReverted: false }, { type: 'REVERT' }),
    ).eql({ isFlipped: false, isReverted: true });
  });

  it('handles FLIP', () => {
    expect(
      reducer({ isFlipped: false }, { type: 'FLIP' }),
    ).eql({ isFlipped: true });
  });
});
